
public class Multiples 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int count = 1;
		System.out.println("N   N*10   N*100   N*1000");
		while(count < 6)
		{
			System.out.println(count + "  " + count*10 +"  "+ count *100+"  "+count*1000);
			count = count + 1;
		}

	}

}
